INSERT INTO meteo.bufr_code (id, bufr_code, description, code_values) VALUES (4289, 20055, 'Состояние неба в тропиках', '[{"code": 3, "description": "Развивающиеся кучевые облака с башнями, которые явно «заваливаются» в подветренную сторону; вертикальная мощность облаков более чем в полтора раза превышает их среднюю ширину."}, {"code": 4, "description": "Развивающиеся кучевые облака с башнями, которые явно «заваливаются» в наветренную сторону; вертикальная мощность облаков более чем в полтора раза превышает их среднюю ширину."}, {"code": 5, "description": "Высокие мощные кучевые облака, вертикальная мощность которых в 2 раза больше их средней ширины; скоплений или гряд не образуют; один или более облачных слоев протянулся от облачных башен, хотя постоянных слоев не наблюдается (см. примечание)."}, {"code": 6, "description": "Отдельные кучево-дождевые облака или большое скопление башенок кучевых облаков, разделенных широкими просветами; основания облаков обычно темные, почти все ячейки ливневогохарактера; могут наблюдаться рассеянные облака верхнего и среднего ярусов; высота отдельныхячеек кучевых облаков в 1–2 раза больше их ширины."}, {"code": 7, "description": "Многочисленные кучевые облака, простирающиеся в средней тропосфере, с разорванными илисплошными слоями облаков среднего яруса и/или перисто-слоистых облаков; башни кучевыхоблаков с высотой обычно не уменьшаются в размере; неровные темные основания облаков,в которых встречаются ячейки ливневого характера."}, {"code": 8, "description": "Сплошная плотная облачность среднего яруса и/или слои перисто-слоистых облаков с неболь-шим количеством больших отдельных кучево-дождевых или мощных кучевых облаков, прони-кающих в эти слои; временами выпадает слабый дождь из высокослоистых облаков; основаниякучево-дождевых облаков неровные и темные с видимыми ячейками ливневого характера (см.примечание)."}, {"code": 9, "description": "Сплошные слои облаков среднего яруса и/или перисто-слоистых облаков с кучево-дождевымии мощными кучевыми облаками, выстроившимися в гряды или образующие полосы облаков;дождь обычно выпадает из слоев высокослоистых облаков, а сильные ливневые дожди — изкучево-дождевых облаков; ветер порывистый."}, {"code": 10, "description": "Состояние неба неизвестно или не описывается ни одной из приведенных выше характеристик."}, {"code": 11, "description": "Зарезервировано"}, {"code": 12, "description": "Зарезервировано"}, {"code": 13, "description": "Зарезервировано"}, {"code": 14, "description": "Зарезервировано"}, {"code": 15, "description": "Отсутствующее значение"}, {"code": 0, "description": "Кучевые облака относительно малых размеров; в основном охватывают менее 2/8 неба;средняя ширина облаков по крайней мере равна их мощности по вертикали."}, {"code": 1, "description": "Кучевые облака средних размеров, охватывающие менее 5/8 неба; средняя ширина облаков больше их мощности по вертикали; в виде вертикальных башен, с признаками незначительныхосадков или без них; обычно облака среднего и верхнего ярусов отсутствуют."}, {"code": 2, "description": "Развивающиеся кучевые облака с быстро растущими в высоту высокими башенками, уменьшающиеся в размере"}]');
