INSERT INTO meteo.bufr_code (id, bufr_code, description, code_values) VALUES (4482, 5502013, 'Характеристика термической разрушенности припая в баллах', '[{"code": 0, "description": "0 баллов (Признаки таяния отсутствуют)"}, {"code": 1, "description": "1 балл однолетний и старый лед (На верхушках холмов, бугров и торосов снег большей частью стаял, на поверхности льда наблюдаются отдельные снежницы в виде тёмных пятен и луж; начался распад сморозей)"}, {"code": 2, "description": "2 балла однолетний и старый лед (Поверхность льда потемнела; снег частично растаял; на поверхности льда появились лужи и отдельные озерки: в бухтах и у наветренных берегов припай покрыт сплошным слоем воды; местами видны небольшие водяные забереги и приустьевые полыньи)"}, {"code": 3, "description": "3 балла однолетний и старый лед (На всей поверхности льда видны четко оконтуренные озерки, снег большей частью растаял; местами видны проталины; в припае появились трещины, а у берегов сквозные забереги; площадь талой воды на поверхности льда уменьшилась в результате стока; лед в стадии обсыхания)"}, {"code": 4, "description": "4 балла однолетний и старый лед (Сильно разрушенный лёд; повсюду видны проталины и промоины, закончен распад сморозей; среди битого льда появились грибовидные льдины с подводными таранами; снежный покров сохранился только в углублениях и около торосов; припай взломан)"}, {"code": 5, "description": "5 баллов однолетний и старый лед (Среди однолетнего льда преобладает битый лёд в виде сильно обтаявших бесформенных глыб, глубоко сидящих в воде; лед сильно пропитан водой, имеет темно-серый цвет. Поля старого льда распались в основном на обломки и глыбы; в результате интенсивного таяния ледяные образования приобрели округленные формы; над водой выступают лишь возвышенные участки; снежный покров отсутствует. Поверхность льдин имеет кружевообразный вид (гнилой лед))"}, {"code": 6, "description": "1 балл молодой лед (Поверхность льда потемнела; снег стаял; наблюдаются единичные проталины и промоины)"}, {"code": 7, "description": "2 балла молодой лед (Сильно разрушенный лёд; повсюду видны проталины; происходит распад ледяных образований на мелкие формы)"}, {"code": 8, "description": "3 балла молодой лед (Ровный лёд вытаял; остались обтаявшие глубоко сидящие в воде торосистые образования)"}, {"code": 9, "description": "Резерв"}]');
