INSERT INTO meteo.bufr_code (id, bufr_code, description, code_values) VALUES (4506, 5515012, 'Характеристика поверхности водоема в баллах', '[{"code": 0, "description": "0б. Зеркально-гладкая поверхность"}, {"code": 1, "description": "1б. Рябь, появляются небольшие гребни волн"}, {"code": 2, "description": "2б. Небольшие гребни волн начинают опрокидываться, но пена не белая, а стекловидная"}, {"code": 3, "description": "3б. Хорошо заметные небольшие волны, гребни некоторых из них опрокидываются, образуя местами белую клубящуюся пену - \"барашки\""}, {"code": 4, "description": "4б. Волны принимают хорошо выраженную форму, повсюду образуются \"барашки\""}, {"code": 5, "description": "5б. Появляются гребни большой высоты, их пенящиеся вершины занимают большие площади, ветер начинает срывать пену с гребней волн"}, {"code": 6, "description": "6б. Гребни очерчивают длинные волны ветровых волн, пена, срываемая с гребней ветром, начинает вытягиваться полосами по склонам волн"}, {"code": 7, "description": "7б. Длинные полосы пены, срываемые ветром, покрывают склоны волн, а местами, сливаясь, достигают их подошв"}, {"code": 8, "description": "8б. Пена широкими, плотными, сливающимися полосами покрывает склоны волн, отчего вся поверхность становится белой, только местами, во впадинах волн, видны свободные от пены участки"}, {"code": 9, "description": "9б. Поверхность воды покрыта плотным слоем пены, воздух наполнен водяной пылью и брызгами, видимость значительна уменьшена"}]');
