INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (219, 'M4091', 'Направление морской деятельности: региональное', 'Напр.морской.деят.рег.', NULL, 'Direction of maritime activities: regional', 'Это сферы морской деятельности, связанные с особенностями отдельных регионов Российской Федерации и мира, под которыми понимается совокупность наиболее значимых для Российской Федерации территорий и акваторий, объединенных общими физико-географическими, экономико-географическими, политико-географическими или военно-географическими характеристиками.  В качестве главных региональных направлений национальной морской политики Российская Федерация выделяет: Атлантическое, Арктическое, Тихоокеанское, Каспийское и Индоокеанское направления. Национальная морская политика строится исходя из их специфических особенностей.', '255A', 'string', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (220, 'M4092', 'Направление морской деятельности: функциональное', 'Напр.морской.деят.функц.', NULL, 'Direction of maritime activities: functional', 'Это сферы морской деятельности в соответствии с их функциональным предназначением, такие как: деятельность государства и общества в области морских перевозок, освоения и сохранения ресурсов и пространств Мирового океана, морской науки, военно-морской и в других областях морской деятельности.', '255A', 'string', NULL);
