INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (562, 'M6151', 'Рейс:квадрат Марсдена', 'Марсден.квадрат', 'число', 'Marsden square', 'Одна из систем номенклатуры площадей, измеряемых 10° широты и 10° долготы, основанная на проекции Меркатора и используемая для определения географического положения и отображения распределения мировых океанографических и метеорологических данных на карте. Каждый квадрат подразделяется на 100 меньших квадратов со сторонами, соответствующими 1°, нумерация которых идет от 00 до 99, причем квадрат 00 является ближайшим к пересечению экватора и гринвичского меридиана. Система предложена в 1831 г. Уильямом Марсденом (William Marsden, 1754—1836), ирландским востоковедом.', '99999', 'int', NULL);
