INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1502, 'P0761_00', 'Состояние пов-ти почвы без снега', 'Почва.без.снег.', 'код', 'State soil surface without snow', 'Оценивается визуально наблюдателем в сроки, ближайшие к 8 и 20 ч. Указывается, является ли почва сухой влажной, мокрой, замёрзшей, покрытой людом, снегом, пылью, песком', '9', 'int', 20062);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1699, 'P0964_00', 'Осадки: количество  (код)', 'Осадки.', 'код', 'Precipitation: quantity', 'Высота слоя воды, образовавшегося на горизонтальной поверхности при выпадении осадков в условиях отсутствия их стока, испарения и просачивания.', '999', 'int', 13011);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (614, 'P0032_00', 'Железо общее', 'Fe.', 'мкг/л', 'Ferrum total', 'Химич. элемент VIII группы периодической системы элементов Д.И. Менделеева. Является одним из самых распространённых элементов после алюминия. Кларк Ж. В земной коре составляет 4,65 %. Входит в состав около 300 минералов, образующих месторождения железных руд. Содержание Ж. в воде лимитируется величиной рН. Двухвалентное Ж. хорошо мигрирует в кислых (рН менее 5,5) водах, слабее - в нейтральных и слабо - в щелочных. При наличии свободного кислорода оксид FeO (Fe2+) неустойчив и легко переходит в Fe2O3 (Fe3+), характеризующийся меньшей миграц. способностью. Процесс окисления Fe2+ во многих случаях протекает с участием железобактерий. В подземных водах при повышении содержания угольной кислоты и отсутствии кислорода находится в виде гидрокарбоната закисного Ж., часто в значительных концентрациях (до нескольких мг/л). При выходе на поверхность при достаточной аэрации гидрокарбонат Ж. гидролизуется, и окисляется до Fe(OH)3. Последний очень мало растворим (при рН 4 растворяется около 0,05 мг/л, а при высоких рН - тысячные доли мг/л), но может присутствовать в растворе в коллоидальном состоянии. Трёхвалентному Ж. свойственна коллоидальная форма миграции с органич. соединениями. В подземных водах содержание ионного Ж. обычно не превышает нескольких мг/л. Только в особо кислых водах, развитых в зонах окисления сульфидных руд и в районах молодого вулканизма, количество Fe3+ достигает нескольких десятков и сотен мг/л. Повыш. содержание Ж. наблюдается также в болотных водах. Воды с концентрацией Ж., превыш. 20 мг/л, относятся к лечебным железистым. В питьевых водах присутствие Ж. нежелательно, так как оно придаёт воде неприятный “железистый” привкус. В связи с этим ПДК Ж. для питьевой воды 0,3 мг/л', '99999.9', 'float', NULL);
