INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (618, 'P0037_00', 'Никель', 'Ni.', 'мг/л', 'Nickel', 'Химический элемент VIII группы периодической системы элементов Д.И.Менделеева. Кларк элемента в земной коре составляет 0,0058 %. Легко растворимы его сульфаты, хлориды и нитраты. В природных водах обычно содержится от сотых до тысячных долей мг/л, в водах рудных месторождений его концентрация достигает 20 мг/л. В подземные горизонты может поступать с водами сточными предприятий горнорудной пром-ти, а также металлургич., машиностроит., металлообрабат., приборостроит., химич. и др., в стоках к-рых его содержание может достигать 400 и даже 1240 мг/л. Считается, что токсичность его соединений невелика. ПДК никеля для питьевой воды составляет 0,1 мг/л', '99.999', 'float', NULL);
