INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (645, 'P0044_00', 'Сульфаты', 'H2SO4.', 'мкг/л', 'Sulphate', 'Ионы сульфатные - sulphate ions. Относятся к числу главных ионов (макрокомпонентов) солевого состава подземных вод. Содержание их в водах лимитируется присутствием ионов кальция, к-рые вместе с И. с. образуют малорастворимый гипс. При невысоких содержаниях кальция в минерализованных водах концентрация SО42- составляет десятки г/л. В отсутствие кислорода (в анаэробной среде) И. с. неустойчивы и восстанавливаются до сероводорода. И. с. часто являются доминирующими в водах с минерализацией от 0,1 до 3-4 г/л. Основными источниками сульфат-ионов в глубоких водоносных горизонтах являются различные осадочные породы, в состав к-рых входят гипс и ангидрит. В грунтовые воды сульфаты могут попадать при разложении организмов растит. и животного происхождения, со сточными водами промышл. предприятий, коммунального хозяйства, с.-х. произ-ва и пр. Отмечается значит. повышение концентрации И. с. в грунтовых водах на территориях крупных городов и в районах животноводч. ферм. В подземных водах крупных промышл. городов необходим прогноз пространственно-временн? го изменения содержания И. с., поскольку повыш. их концентрация (более 300-500 мг/л) оказывает негативное влияние на фундаменты зданий и заглублённые металлич. конструкции в рез-те повышения сульфатной агрессивности подземных вод. ПДК И. с. для питьевой воды составляет 500 мг/л', '99999.999', 'float', NULL);
