INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (860, 'P0157_00', 'Серебро', 'Ag.', 'мкг/л', 'Silver', 'Химический элемент I группы периодической системы элементов Д.И. Менделеева. Кларк элемента в земной коре - 7.10-6 %. В природе встречается в свободном состоянии и в виде примесей преим. в свинцово-цинковых и медных месторождениях. Источником поступления Ag в подземные воды часто служат сточные воды рудников, обогатит. фабрик, фотопредприятий. Повыш. содержание его может быть связано с применением бактерицидных и альгицидных препаратов. В сточных водах может присутствовать в растворённом и взвешенном состояниях, большей частью в виде галлоидных солей. В подземных водах концентрация серебра колеблется от единиц до десятков мг/л. Ионы серебра способны уничтожать бактерии и стерилизовать питьевую воду (нижний предел бактерицидного действия ионов Ag - 2.10-11 моль/л. Роль серебра в организме человека и животных изучена недостаточно. ПДК серебра для питьевых вод - 0,05 мг/л', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (861, 'P0158_00', 'Алюминий', 'Al.', 'мкг/л', 'Aluminum', 'http://www.ecoline.ru/mc/refbooks/hydrochem/ch4-1.html#4.19 К источникам поступления алюминия в природные воды можно отнести: частичное растворение глин и алюмосиликатов, атмосферные осадки, сточные воды различных производств. В природных водах алюминий присутствует в ионной, коллоидной и взвешенной формах. Миграционная способность невысокая. Образует довольно устойчивые комплексы, в том числе органоминеральные, находящиеся в воде в растворенном или коллоидном состоянии. Одним из распространенных соединений алюминия является боксит Al(OH)3. Растворимость его является функцией рН. При значениях рН < 4,5 в растворе преобладают ионы Al3+, при рН 5-6 в растворе преобладают ионы Al(OH)2+, при рН > 7 в растворе преобладают ионы Al(OH)4-. Ионы алюминия обладают токсичностью по отношению к многим видам водных живых организмов и человеку', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (862, 'P0159_00', 'Бериллий', 'Be.', 'мкг/л', 'Berillium', 'Химич. элемент II группы периодич. системы элементов Д.И. Менделеева. Кларк Б. в земной коре - 3,8.10-4 %. Среднее содержание его закономерно возрастает от ультраосновных пород к кислым. Основной рудный минерал - мусковит. Б. мигрирует в подземных водах в основном в виде взвесей и лишь частично в растворенном виде. Хорошо сорбируется коллоидами. Растворимость Б. зависит от рН среды. В кислых водах его содержание может быть повышено, а в щелочных - понижено. Образует комплексные соединения с органич. веществами. Зона интенсивной водной миграции Б. ограничена преим. грунтовыми водами. Повыш. концентрации в воде фтора и органич. веществ способствуют накоплению Б., а наличие в воде кальция препятствует его накоплению. Максим. концентрации Б. в маломинерализ. подземных водах составляют сотые доли мг/ л. Б. и его соединения очень высокотоксичны. Ионы Б. характеризуются общетоксичным, канцерогенным и аллергич. действием. Они замещают биологически важные металлы (магний, марганец и др.), входящие в различные ферменты, и блокируют их действие. ПДК Б. для питьевой воды - 0,0002 мг/л', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (863, 'P0160_00', 'Углерод', 'C.', 'мкг/л', 'Carbon', 'Химический элемент IV группы периодической системы элементов Д.И. Менделеева. Кларк элемента в земной коре составляет 0,10%. В основном находится в связанном состоянии. Важнейшими из минералов У. являются карбонаты. Все горючие соединения представляют собой смесь соединений У. Большое количество У. сосредоточено в виде CO2 в атмосфере, гидросфере, живых организмах. В процессе миграции У. в подземных водах значит/ роль играет система карбонатная, участвующая в его круговороте между литосферой, гидросферой, биосферой и атмосферой. В природных водах У. присутствует в двух формах: минеральной и органич.. Основными источниками поступления карбонатов и гидрокарбонатов в подземные воды являются процессы выветривания и растворения карбонатных пород и метаморфизации продуктов химич. выветривания изверженных пород. В подземных водах концентрация карбонатов редко превышает несколько мг/л, содержание гидрокарбонатов обычно не выше 1 г/л. Максим. содержание гидрокарбонатов (десятки г/л) отмечено в углекислых водах типа Боржоми. В сильно загрязнённых органич. веществами водах концентрация органич. У. может достигать 100 мг/л и более', '99999.99', 'float', NULL);
