INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (865, 'P0192_00', 'Лигнин', 'Лигнин.', 'мкг/л', 'Lignin', 'Лигнин — (от лат. lignum — дерево, древесина) — вещество, характеризующее одеревеневшие стенки растительных клеток. Сложное полимерное соединение, содержащееся в клетках сосудистых растений.', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (866, 'P0162_00', 'Кадмий', 'Cd.', 'мкг/л', 'Cadmium', 'Химич. элемент II группы периодич. системы элементов Д.И. Менделеева. Кларк К. в земной коре составляет 1,3.10-5 % по массе. В воде растворяются сульфат, хлорд и нитрат кадмия. В щелочной среде соединения кадмия выпадают в осадок. В природные воды поступает при выщелачивании почв, полиметаллич. и медных руд, в рез-те разложения водных организмов, способных его накапливать. Соединения кадмия попадают в водоносные горизонты также со сточными водами свинцово-цинковых, автомобильных, самолётостроит. заводов, рудообогатит. фабрик, химич. предприятий и т.д. Кадмий весьма токсичен для человека и животных. ПДК кадмия в питьевой воде составляет 0,001 мг/л.', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (884, 'P0229_07', 'Температура воды: отклонение от нормы', 'Твдоткл.', 'град C', 'Temperature water: anomaly', 'Аномалия температуры воды', '9999.9', 'float', NULL);
