INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (867, 'P0164_00', 'Кобальт', 'Co.', 'мкг/л', 'Cobalt', 'Химич. элемент VIII группы периодич. системы элементов Д.И. Менделеева. Кларк кобальта в земной коре 1,8*10-3 %. В различных условиях проявляет валентность +2 или +3. В воде может мигрировать в виде истинных и коллоидных растворов, суспензий, а также в составе комплексных органич. соединений. Максим. его концентрации в маломинерализ. подземных водах составляют сотые доли мг/ л. Кобальт является необходимым элементом для растений, животных и человека. Биохимич. роль Со заключается в повышении активности различных ферментов, участии в синтезе витамина В12, оказании положит. влияния на углеводный и жировой обмен. При повышенных концентрациях является ядом общетоксического действия. ПДК кобальта для питьевой воды 0,1 мг/л', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (868, 'P0165_00', 'Углекислый газ СО2', 'СО2.', 'мкг/л', 'CO2', 'Диоксид углерода (углекислый газ, угольный ангидрид, углекислота) CO2 - соединение углерода с кислородом, конечный продукт окисления углерода, бесцветный, обладающий слегка кисловатым вкусом и запахом, газ.', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (869, 'P0166_00', 'Хром', 'Cr.', 'мкг/л', 'Chromium', 'Химический элемент VI группы периодич. системы элементов Д.И.Менделеева. Кларк элемента в земной коре составляет 8,3 ·10-3 %. В подземные воды попадает в рез-те выщелачивания из пород и почв, богатых разложившимися организмами и растениями. Значит. количества Cr поступают в водоносные горизонты со сточными водами предприятий машиностроит., металлургич., металлообрабат., автомобильной, станкостроит., кораблестроит., авиационной, текстильной, лакокрасочной, полиграфич., кожевенной, химико-фармацевтич., химич., спичечной и др. отраслей пром-ти. Средняя концентрация Х. в п. в. обычно сотавляет несколько десятков мкг/л, в загрязнённых водах может достигать нескольких сотен мкг/л. Соединения хрома оказывают разнообразное негативное влияние на организм человека: общетоксическое, аллергенное, канцерогенное', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (870, 'P0167_00', 'Медь', 'Cu.', 'мкг/л', 'Copper', 'Химич. элемент I группы периодич. системы элементов Д.И. Менделеева. Кларк элемента в земной коре составляет 4,7*10-3%. Медь присутствует в земной коре в основном в виде соединений с серой. Соединения меди малорастворимы, ион Cu2+ образует как легкорастворимые, так и труднорастворимые соединения. Поведение меди в зоне гипергенеза определяется различной устойчивостью её соединений в зависимости от рН - Eh условий среды. В сильнокислой среде и окислит. условиях содержание М. в п. в. может достигать более 1000 мкг/л, тогда как в щелочных водах и восстановит. условиях концентрация меди обычно не превышает 10 мкг/л. Известно, что медь является активным комплексообразователем и нередко мигрирует в виде коллоидов. Основным источником поступления М. в п. в., кроме естеств. минералов являются воды сточные предприятий химич., металлургич. и др. отраслей пром-ти. Избыт. концентрация меди оказывают неблагоприятное воздействие на растительные и животные организмы. ПДК меди для питьевой воды составляет 1 мг/л, для вод рыбохозяйств. водоемов - 0,05 мг/л', '999.9', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (871, 'P0168_00', 'Углеводороды: нефть растворенная, сумма', 'Нефть.раствор.сумма.', 'мг/л', ' Hydrocarbons: dissolved oil total', NULL, '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (872, 'P0169_00', 'Пестициды: Гамма - ГХЦГ', 'ГХЦГ.', 'мкг/л', 'Pesticides: Gamma', 'гамма-изомер ГХЦГ, ликдан, химический препарат для защиты растений от насекомых — вредителей сельскохозяйственных растений (инсектицид контактного, кишечного и фумигантного действия).', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (873, 'P0193_00', 'Гамма- гексахлор-циклогексан(Линдан)', 'Г-ГХЦГ.Линдан .', 'мг/л', 'Gamma-hexachlorocyclohexane (Lindane)', 'Линдан - это хлорорганический пестицид, которые использовался для борьбы с различными видами насекомых с начала 1950-х годов.', '999.9', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (895, 'P0189_00', 'Пестициды ДДЭ', 'ДДЭ.', 'мкг/л', 'Pesticides: dichlorodiphenylethylene', 'Химическое соединение, используемое для защиты растений, с.-х продуктов, изделий из шерсти, хлопка, кожи, для уничтожения эктопаразитов животных и для борьбы с переносчиками опасных заболеваний', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (896, 'P0194_00', 'Метафос', 'Метафос.', 'мкг/л', 'Metaphos', 'Метафос относится к широко применяемым инсектоакарицидам с контактной токсичностью для большого числа вредных насекомых и клещей. Препарат применяется на зерновых и зернобобовых культурах, на сахарной свекле, на многолетних травах, на льне, подсолнечнике.', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (875, 'P0170_00', 'Ртуть', 'Hg.', 'мкг/л', 'Mercury', 'Химич. элемент II группы периодич. системы элементов Д.И. Менделеева. Кларк элемента в земной коре - 8,3*10-6 %. В подземные воды поступает в рез-те выщелачивания пород в районах ртутных месторождений, разложения водных организмов, накапливающих ртуть. В воде находится во взвешенном и растворённом состояниях, соотношение между этими формами определяется значением рН и химич. составом воды. Содержание Hg в подземных водах составляет десятые доли мкг/л. Значительные количества ртути попадают в водные объекты с водами сточнымипредприятий, производящих красители, пестициды, фармацевтич. препараты, взрывчатые вещества. Соединения ртути высокотоксичны. Они накапливаются в рыбе и могут попадать в организм человека. ПДК ртути для питьевой воды составляет 0,005 мг/л.', '9999.99', 'float', NULL);
