INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (877, 'P0172_00', 'Метоксихлор, хим.', 'Метоксихлор.', 'мг/л', 'Methoxychlor', NULL, '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (878, 'P0173_00', 'Магний', 'Mg.', 'мкг/л', 'Magnesium', 'Магний', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (879, 'P0174_00', 'Марганец', 'Mn.', 'мкг/л', 'Manganese', 'Химич. элемент VII группы периодич. системы элементов Д.И. Менделеева. Кларк элемента в земной коре составляет 0,1%. В подземные воды поступает в рез-те выщелачивания железомарганцевых руд и др. минералов, содержащих марганец. Может поступать с водами сточными марганцевых обогатит. фабрик, металлургич. заводов, предприятий химич. пром-ти, а также с водами шахтными. В большинстве случаев подземные воды содержат марганец в небольшом количестве - сотые и тысячные доли мг/л, реже - единицы мг/л. Марганец и его соединения мало токсичны для людей и животных, однако уже невысокие его концентрации значительно ухудшают органолептич. свойства воды. ПДК марганца для питьевой воды 0,1 мг/л', '9999.9', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (880, 'P0175_00', 'Свинец', 'Pb.', 'мкг/л', 'Lead', 'Элемент IV группы периодической системы элементов Д.И. Менделеева. Кларк элемента в земной коре - 1,6*10-3 %. Наиболее важный минерал свинца - галенит (свинцовый блеск) - имеется во всех сульфидных рудах гидротермального происхождения. С. в п. в. поступает в рез-те растворения эндогенных (галенит) и экзогенных (англезит, церуссит и пр.) минералов. Значит. повышение содержания Pb в окружающей среде связано со сжиганием углей, применением тетраэтилсвинца в моторном топливе, а также со сточными водами рудообогатительных, нек-рых металлургич. и химич. предприятий. В подземных водах концентрация свинца редко достигает нескольких десятков мкг/л. В кислых рудных водах она составляет десятки и сотни мкг/л, лишь в хлоридных термальных водах достигает иногда нескольких мг/л. Pb очень ядовит, накапливается в костях, печени и почках. ПДК свинца для питьевой воды составляет 0,03 мг/л.', '99.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (881, 'P0176_00', 'Сурьма', 'Sb.', 'мкг/л', 'Antimony', 'Химический элемент V группы периодич. системы элементов Д.И. Менделеева. Кларкэлемента в земной коре - 5*10-5 %. Сурьма часто находится в рудах (блеклые руды) в виде комплексных соединений с медью, свинцом и цинком. В подземные воды поступает за счет процессов выщелачивания минералов, содержащих сурьму, и со сточными водами резиновых, стекольных, красильных, спичечных предприятий. Содержание С. в подземных водах обычно не превышает 10 мкг/л. ПДК сурьмы для питьевой воды - 0.05 мг/л', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (882, 'P0177_00', 'Кремний', 'Si.', 'мкг/л', 'Silicon', 'Содержание растворенных в воде соединений кремния, определяемое по концентрации ионов кремнекислоты SiO3', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (883, 'P0178_00', 'Олово', 'Sn.', 'мкг/л', 'Stannous', 'Химический элемент 4 группы периодич. системы элементов Д.И. Менделеева. Его кларк в земной коре составляет 2,5*10-4 %. Наиболее распространенным минералом, содержащим олово, является галенит, к-рый содержится во всех рудах гидротермального происхождения. В подземные воды олово попадает в рез-те процессов растворения эндогенных (галенит) и экзогенных (англезит, церезит и др.) минералов. Повыш. концентрации олова в окружающей среде связаны со сжиганием угля, использованием тетраэтилона в моторном топливе, а также с работой металлургич. предприятий, предприятий химич. и др. отраслей промыш-ти. Концентрация О. в п. в. достигает десятков мкг/л, в водах кислых руд - десятков и сотен мкг/л. Олово является ядовитым элементом, накапливается в костях, печени и почках. В действующих нормативных документах по качеству питьевой воды не нормируется т.к. считается, что концентрации, в к-рых оно может присутствовать в пресной воде, не опасны для здоровья', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (885, 'P0179_00', 'Цинк', 'Zn.', 'мг/л', 'Zinc', 'Химический элемент II группы периодической системы элементов Д.И. Менделеева. Кларк элемента в земной коре составляет около 8,3*10-3 %. В подземные воды попадает в рез-те процессов разрушения и растворения горных пород и минералов, а также со сточными водами рудообогатит. фабрик, гальванич. цехов, производств пергаментной бумаги, минер. красок, вискозы, магнезиального цемента, предприятий химико-фармацевтич., химич., машиностроит., деревообрабат. пром-ти и др. В подземных водах концентрация Zn2+ обычно не превышает десятков мкг/л, а в загрязнённых водах - нескольких мг/л, в рудных и особенно в шахтных водах с низкими значениями рН может достигать сотен мг/л. Многие соединения цинка, прежде всего его сульфат и хлорид, токсичны. В то же время цинк относится к числу биологически активных микроэлементов, влияющих на рост и нормальное развитие растений и организмов. ПДК Zn2+ для питьевой воды составляет 5,0 мг/л.', '99999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (886, 'P0180_00', 'Альфа - ГХЦГ', 'Альфа.ГХЦГ.', 'мкг/л', 'Alpha - Hexachlorocyclohexane', 'Альфа-изомер гексахлорциклогексана', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (887, 'P0181_00', 'Алдрин', 'Алдрин.', 'мкг/л', 'Aldrin', 'Алдрин - пестицид, применявшийся для борьбы с почвенными вредителями. Он широко использовался для защиты злаковых культур, кукурузы и картофеля, а также для защиты деревянных строений от термитов. На территории Российской Федерации алдрин не применялся и не применяется. Алдрин — белое кристаллическое вещество, почти без запаха. Он крайне редко обнаруживается в продуктах питания и тканях живых организмов, а даже если и обнаруживается, то в очень незначительных концентрациях. Алдрин прочно связывается с частичками почвы и практически не попадает в грунтовые воды. Этот пестицид запрещен в Болгарии, Эквадоре, Финляндии, Венгрии, Израиле, Сингапуре, Швейцарии, Турции. Его использование ограничено в Аргентине, Австрии, Канаде, Чили, Европейском Союзе, Японии, Новой Зеландии, Филиппинах, США, Венесуэле.', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (888, 'P0182_00', 'Углеводороды ароматические полициклические, сумма', NULL, 'мкг/л', 'Carbohydrates aromatic polynucleated', 'Органические соединения, молекулы к-рых состоят из углерода и водорода. В зависимости от с т р о е н и я выделяют У. ациклические и циклические. По х а р а к т е р у с в я з и между углеродными атомами У. делятся на насыщенные (предельные), содерж. только простые связи, ненасыщенные, содерж. кратные двойные и тройные связи, и ароматические, содерж. циклы, в к-рых атомы углерода соединены особыми ароматич. связями. У., сходные по строению и характеру связей и отличающиеся только числом метиленовых групп, образуют гомологич. ряды. У. являются основным компонентом большинства нефтей и горючих газов. Нек-рые горючие газы, конденсаты и лёгкие нефти практически состоят только из У.', '99999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (889, 'P0183_00', 'Бэта ГХЦГ', 'Бэта.ГХЦГ.', 'мкг/л', 'Beta  benzene hexachloride', 'бета-изомер гексахлорциклогексана', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (890, 'P0184_00', 'Гексахлоран', 'C6H6Cl6.', 'мкг/л', 'Hexachlorane', 'Гексахлоран  C6H6Cl6 — смесь восьми стереоизомеров 1,2,3,4,5,6-гексахлорциклогексана. Все изомеры представляют собой белые, кристаллические, нерастворимые в воде вещества.  Используется в качестве инсектицида, однако применение гексахлорана для борьбы с насекомыми ограничено.  Препарат, содержащий 99—100 % ?-изомера, называется «линдан».', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (891, 'P0185_00', 'Гептахлор', 'Гептахлор.', 'мкг/л', 'Heptachlor', 'Гептахлор, химический препарат, применяемый в сельском хозяйстве для борьбы с насекомыми-вредителями', '99999.999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (892, 'P0186_00', 'Пестициды ДДД', 'ДДД.', 'мкг/л', 'Pesticides: DDD', 'Химическое соединение, используемое для защиты растений, с.-х продуктов, изделий из шерсти, хлопка, кожи, для уничтожения эктопаразитов животных и для борьбы с переносчиками опасных заболеваний', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (893, 'P0187_00', 'Пестициды ДДЕ', 'ДДЕ.', 'мкг/л', 'Pesticides: DDE', 'Химическое соединение, используемое для защиты растений, с.-х продуктов, изделий из шерсти, хлопка, кожи, для уничтожения эктопаразитов животных и для борьбы с переносчиками опасных заболеваний', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (894, 'P0188_00', 'Пестициды ДДТ', 'ДДТ.', 'мкг/л', 'Pesticides: DDT', 'Химическое соединение, используемое для защиты растений, с.-х продуктов, изделий из шерсти, хлопка, кожи, для уничтожения эктопаразитов животных и для борьбы с переносчиками опасных заболеваний', '99999.9999', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (916, 'P0208_01', 'Фосфор общий: мин', 'Фосфор.общ.вода.мин.', 'мкг/л', 'Phosphorus total: min', 'Содержание всех (неорганических и органических,  растворимых и взвешенных) соединений фосфора в воде', '9999.9', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (897, 'P0195_00', 'Мышьяк', 'As.', 'мкг/л', 'Arsenic', 'Химический элемент V группы периодич. системы элементов Д.И. Менделеева. Кларк М. в земной коре - 1,7*10-4 %. Принадлежит к амфотерным соединениям с преобладанием неметаллических свойств. Мигрирует в виде AsO43- , H2AsO4-, H2AsO3. Амфотерность мвшьяка обусловливает возможность его миграции в широком диапазоне Eh – рН. Аномальные концентрации мышьяка установлены как в кислых, так и в щелочных водах. Так, в сильно кислых сульфатных водах при рН 3,5 и Еh более 0,6 его количество может достигать сотен мг/л. Мышьяк может поступать в воды грунтовые в связи с его широким применением в качестве ядохимиката в сельском и лесном хоз-ве. Содержится также в водах сточных предприятий самых различных отраслей пром-ти - металлургич., химич., стекольной, кожевенной, текстильной, в произ-ве гербицидов, инсектицидов, красок. Мышьяк относится к числу токсических веществ. Нек-рые авторы считают его канцерогенным. ПДК мышьяка для питьевой воды составляет 0,05 мг/л. С другой стороны, мышьяковистые воды используются в качестве лечебных, если концентрация мыьяка более 0,7 мг/л.', '9999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (898, 'P0196_00', 'Нефть в грунте', NULL, 'мг/л', 'Oil: bottom', 'Жидкий каустобиолит, представляющий собой смесь углеводородов метанового, нафтенового и ароматич. рядов с примесью сернистых, азотистых и кислородных соединений. В пластовых условиях Н. содержит также растворённые газы, в основном углеводородного состава. При разведке, эксплуатации, транспортировке, переработке и использовании продуктов переработки Н. является одним из наиболее активных загрязнителей', '9.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (899, 'P0197_00', 'Нефть в грунте: сухой остаток', NULL, 'мг/г', 'Oil: bottom/dry sediment', 'Нефть в грунте: сухой остаток', '9999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (900, 'P0199_00', 'Окисляемость', 'Окисляемость.', 'мг О2/л', 'Oxidability', 'Величина, характеризующая содержание в воде органических и минеральных веществ, окисляемых одним из сильных химических окислителей при определенных условиях. Выражается в миллиграммах кислорода, пошедшего на окисление органических веществ, содержащихся в 1 л воды', '9999.99', 'float', NULL);
