INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (919, 'P0209_00', 'Облачность', 'Облачность.', 'код', 'Clouds high-level', 'Системы взвешенных в атмосфере (не у самой земной поверхности) продуктов конденсации водяного пара - капель воды или кристаллов льда, или тех и других. При укрупнении облачных элементов и возрастании их скорости падения они выпадают из О, в виде осадков. Диаметры капель в О. - от долей микрометра до 200 мкм. Содержание жидкой воды в О. от нескольких сотых долей грамма до нескольких граммов на 1 м3 облачного воздуха. Кроме О. различных типов, наблюдающихся в тропосфере, существуют еще изредка наблюдаемые облака на высотах порядка 20-25 и 70-80 км - перламутровые и серебристые.', '9', 'int', NULL);
