INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (921, 'P0211_00', 'Фенол', 'Фенол.', 'мкг/л', 'Phenol', 'Соединения ароматического ряда, в молекулах к-рых гидроксильные группы связаны с атомами углерода ароматич. кольца. В естественных условиях Ф. образуются при биохимич. окислении и трансформации органич. веществ. Являются одними из наиболее распространённых загрязняющих веществ, поступающих в подземные воды со сточными водами нефтеперерабатывающих, лесохимич., коксохимич., анилинокрасочных и других. предприятий. Концентрация Ф. в незагрязнённых водах или слабозагрязнённых речных водах не превышает обычно 20 мкг/л. ПДК Ф. для питьевой воды (фенольный индекс) составляет 0,25 мг/л', '9999.999', 'float', NULL);
