INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1190, 'P0452_00', 'Кислород: содержание в атмосфере', NULL, 'г/м^3', 'Oxygen: content', 'Химический элемент VI группы периодич. системы элементов Д.И. Менделеева. Имеет самый большой Кларк в земной коре - 47 %. При обычных условиях К. - газ без цвета и запаха, малорастворим в воде. Является одним из основных газов атмосферы (20,95% по объёму). Подавляющая часть атомов К. в земной коре находится в связанном состоянии (99,99%). В составе различных соединений встречается в горных породах, воздухе, природных водах, атмосф. осадках, почве. В подземных водах максим. содержание К., находящегося в виде растворённых свободных молекул О2, может достигать 20 мг/л. Относится к числу наиболее химически активных компонентов', '999.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1191, 'P0453_00', 'Цунами: уровень воды', 'Ур.вод.', 'см', 'Tsunami: level of water', 'Морские волны, возникающие при подводных и прибрежных землетрясениях и приводящие к катастрофическим последствиям.(Океанская волна с периодом от 15 до 60 мин)', '99999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1192, 'P0454_00', 'Явления атмосферные: особые', 'ОАЯ.', 'код', 'Atmospheric phenomenon: the special', 'Дополнительная информация о погоде в срок и между сроками наблюдения (время начала, окончания, продолжительность, изменчивость явлений, ветер, шквал, вихрь, смерч, состояние моря, гололедно-изморозевые отложения, количественные характеристики выпавших осадков, облачность над горами и перевалами, местоположение явлений, скорость и направление их перемещения, оптические и другие явления), к.т.3778', '9999', 'int', NULL);
