INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1262, 'P0509_03', 'Осадки: конвективные ливневые: сумма, анализ', NULL, 'мм', ' Convective storm precipitation: total, analysis', 'Атмосферные осадки — вода в жидком или твёрдом состоянии, выпадающая из облаков или осаждающаяся из воздуха на земную поверхность и различные предметы. Осадки являются главным механизмом перемещения воды из атмосферы на поверхность земли.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1263, 'P0515_10', 'Лавины снежные: сход', 'Лавины.сход.', 'количество', 'Snow avalanche', 'Быстрое, внезапно возникающее движение снега по крутым склонам гор, представляющее угрозу жизни людей и наносящее ущерб объектам экономики', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1264, 'P0516_10', 'Сель', 'Сель.', 'количество', 'Mudflow', 'Сель – временный поток смеси воды и большого количества обломков горных пород от глинистых частиц до крупных камней и глыб, внезапно возникающий в руслах горных рек и лощинах.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1265, 'P0517_10', 'Камнепады', 'Камнепады.', 'количество', 'Rockfalls', 'Камнепады — одна из самых распространенных опасностей, поджидающих туристов в летнее время', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1266, 'P0518_10', 'Оползни', 'Оползни.', 'количество', 'Landslide', 'Оползень - смещение вниз по склону массы рыхлой горной породы под влиянием силы тяжести, особенно при насыщении рыхлого материала водой', '999', 'int', NULL);
