INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1267, 'P0519_10', 'Погода: резкие изменения', 'Погода.резкие.измен.', 'количество', 'Weather: up and down', 'Изменения погоды, при которых ГМЯ по интенсивности не достигают критериев стихийных, но могут затруднить или облегчить в отдельные периоды производственную деятельность основных отраслей народного хозяйства', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1268, 'P0520_10', 'Явления климатически неблагоприятные', 'Явл.климат.неблагопр.', 'количество', 'Climatic neglected phenomenon', 'Сильная жара, засуха, наводнения, ураганы, оттепели, заморозки  и др.', '999', 'int', NULL);
