INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1271, 'P0522_10', 'Наледи', 'Наледи.', 'количество', 'Sleet', 'Естественное ледяное образование, образующееся в результате замерзания речных или подземных вод, излившихся на земную поверхность.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1272, 'P0523_10', 'Суховей', 'Суховей.', 'количество', 'Dry wind', 'Ветер с высокой температурой, низкой относительной влажностью воздуха и большим дефицитом влажности летом в степях и полупустынях.(В течение 3 сут подряд скорость ветра не менее 8 м/с, температура воздуха выше 25 °С, относительная влажность воздуха не более 30% хотя бы в один из сроков наблюдений, дефицит влажности воздуха не менее 40 гПа в 15 ч)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1273, 'P0524_10', 'Засуха атмосферная', 'Засуха.атмосф.', 'количество', 'Drought atmospheric', 'Комплекс факторов: длительное отсутствие эффективных осадков, высокая температура и низкая влажность воздуха в вегетационный период (В течение не менее 30 сут сумма осадков не более 5 мм, максимальная температура воздуха выше 25 °С (в южных районах выше 30 °С), относительная влажность воздуха не более 30%, дефицит насыщения воздуха не менее 40 гПa)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1274, 'P0525_10', 'Засуха почвенная', 'Засуха.почв.', 'количество', 'Drought soil', 'Низкий запас продуктивной влаги в почве в течение длительного времени в вегетационный период (В течение не менее 20 сут подряд запас продуктивной влаги в слое 0-20 см не более 5 мм и/или не более 25 мм в слое 0-100 см)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1275, 'P0526_10', 'Почва: переувлажнение', 'Почва.переувл.', 'количество', 'Soil: overwetting', 'Избыток влаги в почве', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1276, 'P0527_10', 'Заморозки', 'Заморозки.', 'количество', 'Frosts', 'Понижение минимальной температуры воздуха или поверхности почвы до отрицательных значений в период с устойчивой средней суточной температуры воздуха 5 °С и выше (Температура воздуха или поверхности почвы минус 2 °С и ниже*)', '9999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1277, 'P0528_10', 'Половодье', 'Половодье.', 'количество', 'Flood', 'Подъём воды в результате правильного периодического усиления стока (вследствие таяния зимних снегов, ледников, выпадения муссонных дождей)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1278, 'P0529_10', 'Паводок', 'Паводок.', 'количество', 'High water', 'Паводок — фаза водного режима реки, сравнительно кратковременное и непериодическое поднятие уровня воды в реке, возникающее в результате быстрого таяния снега, ледников, обильных дождей.', '999', 'int', NULL);
