INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1280, 'P0530_10', 'Уровень реки высокий (наводнение)', 'Урв.реки.высокий.', 'количество', 'River level: hight (flood)', 'Стихийное затопление суши водой, выступившей из берегов или образовавшейся в результате сильных ливней', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1281, 'P0531_10', 'Сгонно-нагонные явления', 'Урв.сгонно.нагонн.явл.', 'количество', 'Level: surging', 'Сгонно-нагонные явления - повышение или понижение уровня моря или озера под воздействием ветра, дующего со стороны водоема на сушу или наоборот.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1282, 'P0532_10', 'Низкая межень', 'Низкая.межень.', 'количество', 'Low water period', 'Период наиболее низкого уровня воды рек и озер, устанавливающийся после половодья', '999', 'int', NULL);
