INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1292, 'P0534_10', 'Затор', 'Затор.', 'количество', 'Ice jam', 'Скопление битого речного или морского льда, застрявшего в узком канале.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1293, 'P0535_00', 'Зажоры: мощность', 'Зажоры', 'код', 'Ice hanging  dams:power', 'Зажор - явление, сходное с затором льда, но состоящее из скопления рыхлого льда (шуга, небольшие льдинки). Бывают катастрофически, сильные, средние и слабые.', '9', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1294, 'P0535_10', 'Зажор', 'Зажор.', 'количество', 'Ice gorge', 'Скопление шуги с включением мелкобитого льда в русле реки, вызывает стеснение водного сечения, подъем уровня воды и затопление прибрежных участков.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1295, 'P0536_10', 'Цунами', 'Цунами.', 'количество', 'Tsunami', 'Морские волны, возникающие при подводных и прибрежных землетрясениях и приводящие к катастрофическим последствиям.(Океанская волна с периодом от 15 до 60 мин)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1296, 'P0537_10', 'Явление атмосферное', 'Явление.', 'количество', 'Phenomenon', 'Обозначение тех атмосферных явлений, которые визуально наблюдаются на метеорологической станции и в её окрестностях', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1297, 'P0538_10', 'Тягун сильный', 'Тягун.сильный.', 'количество', 'Harbor oscillation', 'Резонансные длинноволновые колебания массы воды в портовых акваториях.(Горизонтальное перемещение судов не менее 1 м)', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1298, 'P0539_10', 'Волнение зыбь', 'Волн.зыбь.', 'количество', 'Long rolling sea', 'Cвободные гравитационные волны, образующиеся из ветровых волн после ослабления или изменения направления ветра либо пришедшие из соседнего района моря, подверженного действию ветра. При полном безветрии 3ыбь называется мертвой. Волна 3ыби, выходя из зоны зарождения, может распространяться на расстояние в нескольких тысяч миль. 3ыбь, выходящая из штормовой области океана, очень долго сохраняет значитительную высоту при сравнительно небольшом увеличении скорости, длины волны и ее периода. Волны 3ыби обычно длинные и пологие, высота может достигать 10 — 15 м, длина — до 300 — 400 м, период — до 15 — 30 с.', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1299, 'P0540_10', 'Лед отрыв', 'Лед.отрыв.', 'количество', 'Separation ice', 'Лед отрыв', '999', 'int', NULL);
