INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1301, 'P0542_00', 'Метан в воздухе: концентрация', 'Метан.концентрация.', 'часть/млрд', 'Methane atmospheric: concentration', 'Метан — простейший углеводород, бесцветный газ без запаха, химическая формула — CH4. Малорастворим в воде, легче воздуха..С воздухом М. образует взрывоопасные смеси, что является осн. причиной взрывов на угольных шахтах', '9.999', 'float', NULL);
