INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1306, 'P0546_10', 'Гололедица', 'Гололедица.', 'количество', 'Ice-covered ground', 'Лед на земной поверхности, образовавшийся после оттепели или дождя в результате наступления похолодания, а также вследствие замерзания мокрого снега, дождя или мороси от соприкосновения с сильно охлажденной поверхностью', '99', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1349, 'P0590_00', 'Волнение ветровое: повторяемость высот волн 6-7метров', 'Повтор.волн.6-7м.', '%', ' Wind waves: frequency of wave heights of 6-7 meters', 'Повторяемость волнения высотой 6-7 метров', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1307, 'P0547_10', 'Град', 'Град.', 'количество', 'Hail', 'Осадки, выпадающие в теплое время года из мощных кучево-дождевых облаков, в виде частичек плотного льда различных, иногда очень крупных, размеров. Г. всегда наблюдается при грозе, обычно вместе с ливневым дождем. Выпадение Г. иногда может дать на земной поверхности покров высотой до 20-30 см. Интенсивный град может уничтожить посевы, иногда наблюдается гибель животных. Не следует смешивать Г. с ледяной крупой.', '99', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1308, 'P0548_1', 'Гроза', 'Гроза.', NULL, 'Storm (thunder)', 'Комплексное атмосферное явление, необходимой частью которого являются многократные электрические заряды между облаками или между облаком и землей (молнии) , сопровождающиеся звуковым явлением - громом. Г. связана с развитием мощных кучево-дождевых облаков, следовательно, с сильной неустойчивостью стратификации воздуха при высоком влагосодержании. Поэтому Г. характеризуется еще сильными шквалистыми ветрами и ливневыми осадками, нередко с градом. Явление сравнительно недолговременное, отдельная Г. редко продолжается более 2 ч. Различают Г. фронтальные и внутримассовые (в том числе тепловые).', 'A', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1309, 'P0548_10', 'Гроза: количество', 'Гроза.', 'количество', 'Storm (thunder)', 'Комплексное атмосферное явление, необходимой частью которого являюися многократные  электрические заряды между облаками или между облаком и землей ( молнии), сопровождающиеся звуковым явлением - громом', '999', 'int', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1310, 'P0549_10', 'Оттепель', 'Оттепель.', 'количество', 'Thaw', 'повышение температуры до положительных значений в устойчивый морозный период', '999', 'int', NULL);
