INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1312, 'P0551_10', 'Сильная жара', 'Жарасильн.', 'количество', 'Intense heat', 'Высокая максимальная температура воздуха в течение продолжительного времени.Максимальная температура воздуха не менее 35 °С в течение более 5 сут', '999', 'int', NULL);
