INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1314, 'P0553_10', 'Буря пыльная сильная', 'Буря.пыль.сильн.', 'количество', 'Heavy storm, duststorm', 'Перенос пыли или песка при сильном ветре, вызывающий значительное ухудшение видимости.Средняя скорость ветра не менее 15 м/с, МДВ не более 500 м', '999', 'int', NULL);
