INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1316, 'P0555_10', 'Гололедно-изморозевые отложения сильные', 'Гололёд.сильн.', 'количество', 'Glaze-ice and rime deposition (strong)', 'Слой плотного льда (матового или прозрачного), нарастающего на поверхности земли и на предметах преимущественно с наветренной стороны, от намерзания капель переохлажденного дождя или мороси. Обычно наблюдается при температурах от 0 до -30 , реже при более низких, до -160. Корка намерзшего льда может достичь толщины нескольких сантиметров и вызвать обламывание сучьев, обрыв проводов и т.п.', '999', 'int', NULL);
