INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1331, 'P0573_00', 'Прилив: высота', 'ПРВ.', 'см', 'Tide: height', 'Высота прилива-отлива относительно условного среднего уровня моря, который определяется в  результате осреднения больших рядов срочных наблюдений', '9999.9', 'float', NULL);
