INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1395, 'P0633_10', 'Кислород: количество наблюдений', NULL, 'мл/л', ' Oxygen: number of observations', 'Химический элемент VI группы периодич. системы элементов Д.И. Менделеева. Имеет самый большой Кларк в земной коре - 47 %. При обычных условиях К. - газ без цвета и запаха, малорастворим в воде. Является одним из основных газов атмосферы (20,95% по объёму). Подавляющая часть атомов К. в земной коре находится в связанном состоянии (99,99%). В составе различных соединений встречается в горных породах, воздухе, природных водах, атмосф. осадках, почве. В подземных водах максим. содержание К., находящегося в виде растворённых свободных молекул О2, может достигать 20 мг/л. Относится к числу наиболее химически активных компонентов', '99.99', 'float', NULL);
