INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1414, 'P0643_00', 'Сероводород%', 'H2S.S.проц.', '%', 'Hydrogen sulphide %', 'Соединение серы с водородом (Н2S), при обычных условиях бесцветный горючий газ с запахом тухлых яиц. С. хорошо растворим в воде - в 1 л воды растворяется 2,58 л H2S. В воде находится в виде недиссоциир. молекул Н2S, ионов гидросульфида НS- и весьма редко - ионов сульфида S2-. Соотношение концентраций этих форм определяется значением рН воды. При рН>10 содержанием ионов S2- можно пренебречь, при рН 7 содержание Н2S и НS- примерно одинаково, при рН 4 сероводород почти полностью (99,8%) находится в виде недиссоциир. молекул. Подземные воды с содержанием С. более 10 мг/л относятся к лечебным. В приповерхностных условиях С. образуется в восстановит. процессах при бактериальном разложении органич. веществ. Присутствие С. в подземных водах является показателем восстановит. обстановки среды. С. высокотоксичен. Тяжёлые отравления со смертельным исходом могут наступить при концентрации Н2S в воздухе 0,01 мг/л. Область взрывоопасных концентраций составляет 4,3 - 45,5%. В питьевой воде содержание С. не допускается. Причиной этого является его высокая токсичность, а также резкий запах, к-рый значительно ухудшает органолептич. свойства воды, делая её непригодной для питьевого водоснабжения и др. технич. и хозяйств. целей. Для водных объектов санитарно-бытового и рыбохозяйств. назначения наличие Н2S и сульфидов в воде не допускается', '99.99', 'float', NULL);
INSERT INTO meteo.esimo_elem (id, esimo_name, full_title_ru, short_title_ru, unit, full_title_eng, description, obj_size, obj_type, bufr_descr) VALUES (1415, 'P0644_00', 'Прозрачность воды', 'Вода.прзрч.', 'м', 'Water transparency', 'Показатель, характеризующий способность воды пропускать световые лучи. Зависит от содержания в ней различных окрашенных взвешенных минер. и органич. веществ. Мерой П. в. служит толщина столба воды, при к-рой можно наблюдать белый диск-прозрачномер.', '99', 'int', NULL);
